Declare @Schema nvarchar(20) = '@{item().Schema}';
Declare @TableName nvarchar(200) = '@{item().EntityName}';
Declare @Incremental bit = @{if(equals(item().LastCopyMarker,null),0,1)};
declare @ChildItems nvarchar(max)='@{activity('CheckForNewFiles').output.childItems}'; 
declare @ToDateTime DateTime2='@{variables('ToDateTime')}'; 

Declare @DataLocation varchar(2000);

select top 1  
	@DataLocation = DataLocation
from 
	_ControlTableForCopy 
where 
	TableSchema = @Schema
	and TableName =  @TableName

-- Update status to 1- In Progress 
Update _ControlTableForCopy
	set LastCopyStatus = 1
where 
	TableSchema = @Schema
	and TableName =  @TableName

declare @LastFileName nvarchar(200);

SELECT 
	@LastFileName = max(name) 
FROM Openjson(@ChildItems) 
WITH 
	(name nvarchar(100) '$.name') 
WHERE 
	name != 'index.json';  

-- full export, create a new table from staging table schema, copy the data and then swap the object
IF (@Incremental = 0)
BEGIN

	declare @FullCopyTemplate nvarchar(max) = 'Create Table {schema}._new_{TableName} with (Distribution = Hash(RecId)) 
	as Select top 0 * from {schema}.{TableName}

	Copy Into {schema}._new_{TableName}
	From ''{DataLocation}''
	WITH (
		 FILE_TYPE = ''CSV'',
		 CREDENTIAL = (IDENTITY = ''Managed Identity'')
	 );

	RENAME OBJECT::{schema}.{TableName}  TO _old_{TableName};
	RENAME OBJECT {schema}._new_{TableName} TO {TableName};
	DROP TABLE {schema}._old_{TableName};

	UPDATE  [dbo].[_ControlTableForCopy] 
	SET 
		LastCopyDateTime =(SELECT max([DataLakeModified_DateTime]) FROM {schema}.{TableName}), 
		LastCopyMarker = ''{LastFileName}'',
		LastCopyStatus = 0
	WHERE 
		TableSchema = ''{Schema}'' 
		AND TableName = ''{TableName}''  

	'

	declare @FullCopyStatement nvarchar(max) = replace(replace(replace(replace(@FullCopyTemplate, '{Schema}', @Schema)
													, '{TableName}', @TableName)
												, '{DataLocation}', @DataLocation)
												, '{LastFileName}', @LastFileName) ;
	Print(@FullCopyStatement);
	Execute sp_executesql  @FullCopyStatement;

END 

-- Incremental Table Create, Copy Into, Dedplicate and Merge
ELSE
	BEGIN;
		Declare @CDCDataLocation varchar(2000);
		Declare @CDCColumnList varchar(max);
		declare @UpdateColumns nvarchar(max);
		declare @InsertColumns nvarchar(max);
		declare @InsertValues nvarchar(max);


		set @CDCDataLocation = concat(SubString(@DataLocation, 0, CharIndex('/Tables/', @DataLocation)) , '/ChangeFeed/', @TableName, '/*.csv')

		Select 
			@CDCColumnList = ColumnList 
		from _sqlmetadata 
		where 
			EntityName = '_cdc_' + @TableName;

		Select
			@UpdateColumns = STRING_AGG(CONVERT(NVARCHAR(max), 'T.' + COLUMN_NAME + ' = S.'  + COLUMN_NAME), ','),
			@InsertColumns = STRING_AGG(CONVERT(NVARCHAR(max), COLUMN_NAME), ',')
		FROM INFORMATION_SCHEMA.COLUMNS 
		WHERE TABLE_NAME = @TableName
		and  TABLE_SCHEMA = @Schema

		set @UpdateColumns = replace(replace( @UpdateColumns, 'T._SysRowId = S._SysRowId,T.LSN = S.LSN', 'T._SysRowId = S.RecId,T.LSN = S.Start_LSN'), ',T.RecId = S.RecId', '');
		set @InsertValues = replace(@InsertColumns, '_SysRowId,LSN', 'RecId,Start_LSN');

		declare @IncrementalCopyTemplate nvarchar(max) =
		'print(''Create Table : {Schema}._cdc_{TableName}'' );

		create table {Schema}._cdc_{TableName} ({CDCColumnList})
		With (Distribution = Hash(RecId))

		print(''Copy Into : {Schema}._cdc_{TableName} {CDCColumnList}'' );
		Copy Into {Schema}._cdc_{TableName} 
			From ''{CDCDataLocation}''
			WITH (
					FILE_TYPE = ''CSV'',
					CREDENTIAL = (IDENTITY = ''Managed Identity'')
				)

		print(''Deduplicate Data: {Schema}._cdc_{TableName}'' );

		Delete FROM X
		FROM 
		(
			SELECT 
			ROW_NUMBER() OVER (PARTITION BY RECID ORDER BY Start_LSN, Seq_Val, DataLakeModified_DateTime DESC) AS ROWNO,
			*
			FROM {Schema}._cdc_{TableName}

		) AS X
		WHERE X.ROWNO > 1

		print(''Merge Data: {Schema}._cdc_{TableName}'' );

		MERGE {Schema}.{TableName} T USING {Schema}._cdc_{TableName}  S
			ON T.RECID = S.RECID
		  WHEN MATCHED and S.DML_Action <> ''DELETE''
			THEN UPDATE SET 
				{UpdateColumn}
		  WHEN NOT MATCHED BY TARGET and S.DML_Action <> ''DELETE''  
			THEN INSERT ({InsertColumn})
			Values ({InsertValues})
		  WHEN MATCHED and S.DML_Action = ''DELETE''
			 THEN DELETE;

		UPDATE  [dbo].[_ControlTableForCopy] 
		SET 
			LastCopyDateTime =(SELECT max([DataLakeModified_DateTime]) FROM {schema}._cdc_{TableName}), 
			LastCopyMarker = ''{LastFileName}'',
			LastCopyStatus = 0
		WHERE 
		TableSchema = ''{Schema}'' 
		AND TableName = ''{TableName}''   


		Drop Table {Schema}._cdc_{TableName}
		'

		declare @IncrementalCopyStatement nvarchar(max) = replace(replace(
																	replace(
																		replace(
																				replace(
																						replace(
																								replace(
																										replace(@IncrementalCopyTemplate, '{Schema}', @Schema)
																								, '{TableName}', @TableName)
																						, '{CDCColumnList}', @CDCColumnList)
																				, '{CDCDataLocation}',@CDCDataLocation)
																		, '{UpdateColumn}', @UpdateColumns)
																	, '{InsertColumn}', @InsertColumns)
																,'{InsertValues}',@InsertValues)
																, '{LastFileName}', @LastFileName) ;

		print(@IncrementalCopyStatement);
		Execute sp_executesql  @IncrementalCopyStatement;
	END;

